(40, 'storefront-customize', 'info', 'en_US', 'Design your store with Storefront 🎨', 'Visit the Storefront settings page to start setup and customization of your shop.', '{}', 'unactioned', 'storefront', '2022-09-11 10:51:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(41, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-13 13:08:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-13 13:08:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-13 13:08:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-09-13 13:08:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-13 13:08:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'custom_deposit_schedules_inbox', 'marketing', 'en_US', 'Customize your deposit schedule', 'Receive your funds according to your own business needs with custom deposit schedules from WooCommerce Payments. You’ll have the flexibility to deposit funds into your bank account daily, weekly, monthly, or even on-demand – you’ve done the work, now get paid on your terms.', '{}', 'pending', 'woocommerce.com', '2022-09-15 04:31:26', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'wc-admin-personalize-store', 'info', 'en_US', 'Personalize your store\'s homepage', 'The homepage is one of the most important entry points in your store. When done right it can lead to higher conversions and engagement. Don\'t forget to personalize the homepage that we created for your store during the onboarding.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-16 16:56:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-09-19 12:07:18', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'googlelistings_signals2022_hasGLA', 'marketing', 'en_US', 'Show off your products with Ads on Google', 'You’re ready to grow with ads. Google optimizes for performance across your products; you only pay for results. If you’re new to Google, you can earn up to $500 in ad credits (T&amp;Cs apply). <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">Contact support</a> if you need guidance with Google Listings &amp; Ads.', '{}', 'pending', 'woocommerce.com', '2022-09-23 16:32:45', NULL, 0, 'plain', '', 0, 0, 'info'),
